@push('script')
<script>
  document.getElementById('english_languange_education').addEventListener('click', function(event) {
          click_tab(1) 
      });
  const click_tab = (index)=>{
    event.preventDefault(); // Mencegah perilaku default link

          const tabs = document.querySelectorAll('.tab-foe li');
          
          tabs.forEach(function(tab) {
              tab.classList.remove('active');
          });

          const content_tab = document.querySelectorAll('.box-content .content-tab');
          content_tab.forEach(function(content) {
              content.classList.remove('active');
          });
          tabs[index].classList.add('active'); 
          content_tab[index].classList.add('active'); 
  }
</script>
@endpush
